fn solve(input: &str) -> i32 {
    let mut floor_counter = 0;
    // TODO: redo with iterator
    for c in input.chars() {
        if c == '(' {
            floor_counter = floor_counter + 1;
        } else {
            floor_counter = floor_counter - 1;
        }
    }
    return floor_counter;
}

