    use super::*;
    #[test]
    fn simple_1() {
        let input = "(())";
        let result = 0;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_2() {
        let input = "()()";
        let result = 0;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_3() {
        let input = "(((";
        let result = 3;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_4() {
        let input = "(()(()(";
        let result = 3;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_5() {
        let input = "))(((((";
        let result = 3;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_6() {
        let input = "())";
        let result = -1;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_7() {
        let input = "))(";
        let result = -1;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_8() {
        let input = ")))";
        let result = -3;
        assert_eq!(solve(input), result);
    }
    #[test]
    fn simple_9() {
        let input = ")())())";
        let result = -3;
        assert_eq!(solve(input), result);
    }

